<template>
  <div class='main'>
    <Modal
      v-model="modal1"
      title="学校添加"
      @on-cancel="modelcancel"
      :mask-closable='false'>
      <Card>
      <Form ref="formInline" :model="formInline" :rules="ruleInline" :label-width="120">
        <FormItem prop="title" label="标题">
          <Input v-model="formInline.title" placeholder="请输入标题"></Input>
        </FormItem>
        <FormItem prop="descript" label="描述">
          <Input v-model="formInline.descript" type="textarea" placeholder="请填写描述"></Input>
        </FormItem>
        <FormItem label="国家/地区">
          <Select v-model="formInline.country" style="width:160px">
            <Option value="北美">北美</Option>
            <Option value="英国">英国</Option>
            <Option value="欧洲">欧洲</Option>
            <Option value="香港/新加坡">香港/新加坡</Option>
            <Option value="澳洲">澳洲</Option>
            <Option value="日本">日本</Option>
            <Option value="其他">其他</Option>
          </Select>
        </FormItem>
        <Divider />
        <FormItem label="标题图片上传">
          <img style="width: 160px" :src="articleimg" onerror='this.src="data:image/jpeg;base64,"'/>
          <Upload action="/dataserver/modo/article/upload.php"
                  ref="uploadimg"
                  :format="['jpg','jpeg','png']"
                  :data="{type:'article_img'}"
                  :on-format-error="imgformaterror"
                  :on-success="imgsuccess"
                  :show-upload-list="false"
                  :max-size="2048">
            <Button style="width: 160px" icon="ios-camera">上传图片</Button>
          </Upload>
        </FormItem>
        <FormItem label="文章上传">
          <Upload action="/dataserver/modo/article/upload.php"
                  ref="upload_article"
                  :format="['html','htm']"
                  :data="{type:'article'}"
                  :on-format-error="articleformaterror"
                  :on-success="articlesuccess"
                  :show-upload-list="false"
                  :max-size="20480">
            <Button style="width: 160px" icon="ios-cloud-upload-outline">上传文章</Button>
            <Button type="primary" :disabled="noneArticle" @click="previewhtml">预览文章</Button>
          </Upload>
        </FormItem>
        <FormItem>
          <Button style="width: 200px" type="primary" @click="submit('formInline')">提交</Button>

        </FormItem>
      </Form>
    </Card>
      <div slot="footer">
      </div>
    </Modal>
    <Card class="add">
      <p slot="title">学校添加</p>
      <Button left type="primary" @click="addArticle">添加</Button>
    </Card>
    <card class="table">
      <p slot="title">
        学校列表
      </p>
      <Table class="table" height="600" highlight-row border :columns="thead" :data="tdata"></Table>
    </card>
  </div>
</template>

<script>
import loader from '../utils/loader'
import date from '../utils/date'
export default {
  name: 'articleinfo',
  props: [],
  data () {
    return {
      articleimg: '', // 'data:image/jpeg;base64,',
      article: '',
      status: 'add',
      currentID: '',
      index: -1,
      noneArticle: true,
      formInline: {
        title: '',
        descript: '',
        country: '其他'
      },
      ruleInline: {
        title: [
          { required: true, message: '请输入入标题', trigger: 'blur' },
          { type: 'string', min: 1, message: '不能少于一个字符', trigger: 'blur' }
        ],
        descript: [
          { required: true, message: '请填写描述', trigger: 'blur' },
          { type: 'string', min: 6, message: '不能少于六个字符', trigger: 'blur' }
        ],
        country: [
          { required: true, type: 'string', message: '请填写描述', trigger: 'blur' }
        ]
      },
      modal1: false,
      thead: [
        {
          title: '标题',
          width: 160,
          key: 'title',
          render: (h, params) => {
            return h('div', [
              /* h('Icon', {
                props: {
                  type: 'ios-ribbon-outline'
                }
              }), */
              h('strong', params.row.title)
            ])
          }
        },
        {
          title: '描述',
          key: 'descript'
        },
        {
          title: '标题图片',
          width: 100,
          key: 'image',
          render: (h, params) => {
            return h('img', {
              attrs: {
                src: params.row.url
              },
              style: {
                width: '60px'
              }
            })
          }
        },
        {
          title: '学校',
          key: 'article'
        },
        {
          title: '国家/地区',
          width: 100,
          align: 'center',
          key: 'country'
        },
        {
          title: '日期',
          width: 100,
          key: 'date',
          sortable: true

        },
        {
          title: '操作',
          key: 'action',
          width: 150,
          align: 'center',
          render: (h, params) => {
            return h('div', [
              h('Button', {
                props: {
                  type: 'warning',
                  size: 'small'
                },
                style: {
                  marginRight: '5px'
                },
                on: {
                  click: () => {
                    this.show(params.index)
                  }
                }
              }, '修改'),
              h('Button', {
                props: {
                  type: 'error',
                  size: 'small'
                },
                on: {
                  click: () => {
                    this.remove(params.index)
                  }
                }
              }, '删除')
            ])
          }
        }
      ],
      tdata: [
        /* {
          title: '美国学校',
          descript: 'AAAAAAAAAA',
          url: 'https://file.iviewui.com/dist/e1cf12c07bf6458992569e67927d767e.png',
          article: 'https://file.iviewui.com/dist/e1cf12c07bf6458992569e67927d767e.png',
          country: '北美',
          date: '2019-05-06'
        } */
      ]
    }
  },
  components: {},
  methods: {
    // 该属性中申明方法
    show (index) {
      this.status = 'modify'
      this.modal1 = true
      this.index = index
      this.formInline.title = this.tdata[index].title
      this.formInline.descript = this.tdata[index].descript
      this.formInline.country = this.tdata[index].country
      this.articleimg = this.tdata[index].url
      this.article = this.tdata[index].article

      console.log(this.article)
      if (this.article && this.article !== '') {
        this.noneArticle = false
      } else {
        this.noneArticle = true
      }

      this.currentID = this.tdata[index].id
    },
    remove (index) {
      // this.data6.splice(index, 1)
      this.currentID = this.tdata[index].id
      loader.post('/dataserver/modo/article/d_article.php', {
        id: this.currentID
      }, (e) => {
        console.log(e)
        console.log(index)
        this.tdata.splice(index, 1)
      })
    },
    submit (name) {
      this.$refs[name].validate((valid) => {
        if (valid) {
          this.$Message.success('Success!')

          if (this.status === 'add') {
            loader.post('/dataserver/modo/article/i_article.php', {
              title: this.formInline.title,
              descript: this.formInline.descript,
              url: this.articleimg,
              article: this.article,
              country: this.formInline.country
            }, (e) => {
              let result = e.data
              console.log(result)
              if (result.result === '1') {
                this.tdata.push({
                  id: result.id,
                  title: this.formInline.title,
                  descript: this.formInline.descript,
                  url: this.articleimg,
                  article: this.article,
                  country: this.formInline.country,
                  date: date.getNowFormatDate()
                })
                // console.log(this.tdata)
                // this.tdata = this.tdata
              }
            })
          } else {
            loader.post('/dataserver/modo/article/u_article.php', {
              id: this.currentID,
              title: this.formInline.title,
              descript: this.formInline.descript,
              url: this.articleimg,
              article: this.article,
              country: this.formInline.country
            }, (e) => {
              console.log(e)
              let obj = this.tdata[this.index]
              obj.title = this.formInline.title
              obj.descript = this.formInline.descript
              obj.url = this.articleimg
              obj.article = this.article
              obj.country = this.formInline.country
              this.index = -1
            })
          }
        } else {
          this.$Message.error('Fail!')
        }
      })
    },
    modelcancel () {
      this.status = 'add'
      this.article = ''
      this.articleimg = ''
      this.$refs['formInline'].resetFields()
    },
    previewhtml () {
      window.open(this.article, '_blank')
    },
    addArticle (e) {
      console.log(e)
      this.modal1 = true
      this.status = 'add'
      this.article = ''
      this.articleimg = ''
      this.noneArticle = true
      this.$refs['formInline'].resetFields()
    },
    imgsuccess (response, file, fileList) {
      if (response) {
        if (response.result === '1') {
          this.$Message.success('文章图片上传成功！')
          this.articleimg = response.url
          console.log(this.articleimg)
        }
      }
      console.log(response)
    },
    articlesuccess (response, file, fileList) {
      if (response) {
        if (response.result === '1') {
          this.$Message.success('文章上传成功！')
          this.article = response.url
          this.noneArticle = false
        }
      }
      console.log(response)
    },
    imgformaterror () {
      this.$Message.error('只支持[ jpg ,jpeg ,png ]格式')
    },
    articleformaterror () {
      this.$Message.error('只支持[ html ]格式')
    },
    getArticleList (result) {
      this.tdata = result.data
      console.log(result.data)
    }
  },
  mounted: function () {
    // 钩子函数
  },
  computed: {
    // 计算属性，当数据发生变化时才调用，否则直接调用缓存。提升性能
  },
  watch: {
    // 监测变化。
  },
  created () {
    loader.post('/dataserver/modo/article/article.php', {}, this.getArticleList)
  }

}
</script>

<style scoped>
.main{
  border: 0px solid #d7dde4;
}
.table table{
// table-layout: auto;
  width: 100% !important;
}
</style>
